mapper.select:
select  * from t_company c
left   join t_means m on m.id=c.companyDataId
limit #{start},#{limit}

select c.*,p.*,m.*,l.*,ch1.Id as ch1Id,ch1.Name as ch1Name,ch1.Pid as ch1Pid,ch1.regionId as ch1RegionId,ch1.type as ch1Type,ch1.chinaStart as ch1ChinaStart,ch2.Id as ch2Id,ch2.Name as ch2Name,ch2.Pid as ch2Pid,ch2.regionId as ch2RegionId,ch2.type as ch2Type,ch2.chinaStart as ch2ChinaStart,ca.lineId as lineId2,ca.priceId as priceId2,ca.xhId,ca.endHour,t.departureTime,ca.giveHome,ca.lowPrice,ca.openHour,ca.postStatus,ca.takeHome from t_company c LEFT JOIN capacity ca on ca.lineId=c.companyCircuitId left join t_line l on l.lineId=c.companyCircuitId left join china ch1 on ch1.id=l.startStation left join china ch2 on ch2.id=l.endStation LEFT JOIN pricelist p on p.priceId=ca.priceId LEFT JOIN t_means m on m.id=c.companyDataId LEFT JOIN timetable t on t.fcsjId=ca.fcsjId where 1=1
and c.companyName like '%${companyBean.companyName}%'
and l.startStation=#{companyBean.startStation}
and l.endStation=#{companyBean.endStation}
